void solveRubix ( );
void writeOut ( FILE * , int );
fpos_t getGUID ( FILE * );

int main ( int argc , char ** argv ) {

	solveRubix ( );
	printf ( "I like to put myself back together\n" );
	return 0;
}

void solveRubix ( ) {
	unsigned short id = data[8]<<8 | data[9];
	unsigned short total_ids = data[10]<<8 | data[11];
	//unsigned long payload_bytes = data[12] << 24 | data[13] << 16 | data[14] << 8 | data[15];
	//payload can be up to 2984
	char name[8] = "payload";
	char beacon[10] = {0xAA,0xAA,0xAA,0xAA,0xAA,0xAA,0xAA,0xAA,0xAA,0xAA};
	unsigned len = 0;
	unsigned char in[4];
	in[0] = data[15];
	in[1] = data[14];
	in[2] = data[13];
	in[3] = data[12];

	unsigned payload_bytes = 0x0;

	memcpy ( &payload_bytes , in, 4);
	
	printf ( "%u\n" , id );
	printf ( "%u\n" , total_ids );
	printf ( "%u\n" , payload_bytes );
	
	FILE * fp = fopen ( name , "wb+" );

	if ( !fp ) {
		printf ( "Unable to write to file %s\n" , name );
	}
			
	for ( len = 0 ; len <  total_ids; len ++ ) {
		if ( len == id ) {
			fwrite ( data + 16, sizeof(char), payload_bytes, fp );
		} else { 
			writeOut ( fp , len );	
		}
	}

	

	fclose ( fp );
	char filename[10];
	sprintf ( filename, "%d" , id );

	if ( remove ( filename ) ) {
		printf ( "ERROR DELETING FILE\n" );
	}
}

void writeOut ( FILE * fp , int id ) {
	char buffer[10];
	char data_[3000];
	sprintf ( buffer , "%d" , id );

	FILE * fid = fopen ( buffer , "r" );

	if ( !fid ) {
		printf ( "Unable to write to file %s\n" , buffer );
	}

	fpos_t startpos = getGUID ( fid );
	
	fread ( data_, sizeof(char), 2990, fid );

	fclose ( fid );

	unsigned char count[2];
	count[0] = data_[0];
	count[1] = data_[1];

	unsigned short count_id=0;
	memcpy ( &count_id , count , 2 );

	unsigned char idd_[2];
	idd_[0] = data_[3];
	idd_[1] = data_[2];

	unsigned short idd;
	memcpy ( &idd , idd_, 2);
	unsigned char inn[4];
	inn[0] = data_[7];
	inn[1] = data_[6];
	inn[2] = data_[5];
	inn[3] = data_[4];

	unsigned payload_byttes = 0x0;

	memcpy ( &payload_byttes , inn, 4);
	
	printf ( "Payload_ID: %u\n" , count_id );
	printf ( "Total_IDS: %u\n" , idd );
	printf ( "PAYLOAD: %u\n" , payload_byttes );
	
	fwrite ( data_+8, sizeof(char), payload_byttes, fp );
	if ( remove ( buffer ) ) {
		printf ( "ERROR DELETING FILE\n" );
	}
}

fpos_t getGUID ( FILE * fcid ) {
	int pos = 0;
	fpos_t poss;
	
	while ( 1 ) {
		unsigned cc = 0;
		fread ( &cc , sizeof(char), 1, fcid );
		if ( cc == data[pos] ) {
			pos++;
		} else {
			pos = 0;
		}
		if ( pos == 8 ) {
			fgetpos ( fcid , &poss );
			break;
		}
	}
	return poss;

}